import * as React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';
import PropTypes from 'prop-types';
import Button from '@mui/material/Button';
import DialogTitle from '@mui/material/DialogTitle';
import Dialog from '@mui/material/Dialog';
import PersonIcon from '@mui/icons-material/Person';
import AddIcon from '@mui/icons-material/Add';
import { blue } from '@mui/material/colors';
import { DocumentScannerOutlined } from '@mui/icons-material';


const emails = ['username@gmail.com', 'user02@gmail.com'];
function SimpleDialog(props) {
    const { onClose, open } = props;

    const handleClose = () => {
        onClose();
    };

    const handleListItemClick = (value) => {
        onClose(value);
    };

    return (
        <Dialog onClose={handleClose} open={open}>
        <DialogTitle>Personal Info</DialogTitle>
        <List sx={{ pt: 0 }}>
            {emails.map((email) => (
            <ListItem>
                <ListItemAvatar>
                <Avatar sx={{ bgcolor: blue[100], color: blue[600] }}>
                    <PersonIcon />
                </Avatar>
                </ListItemAvatar>
                <ListItemText primary={email} />
            </ListItem>
            ))}

            <ListItem autoFocus>
            <ListItemAvatar>
                <Avatar>
                <AddIcon />
                </Avatar>
            </ListItemAvatar>
            <ListItemText primary="Add account" />
            </ListItem>
        </List>
        </Dialog>
    );
}

SimpleDialog.propTypes = {
    onClose: PropTypes.func.isRequired,
    open: PropTypes.bool.isRequired,
};

export default function EmployeeList(props) {

    const [open, setOpen] = React.useState(false);
    const [cursor, setCursor] = React.useState('crosshair');
    const radioGroupRef = React.useRef(null);

    const handleClickOpen = () => {
        setOpen(true);
    };
    const handleClose = () => {
        setOpen(false);
    };
    const handleMouseEnter = () => {
        // e.target.style.background = 'silver';
        // let temp = document.getElementById('list').children
        // document.write( temp )
        // for (let i=0;i<document.getElementById('list').children.length;i++) {
        //     document.getElementById('list').children[i].style.background = 'silver';
        // }
        radioGroupRef.current.focus();
        setCursor('pointer');
    };
    const handleMouseLeave = () => {
        setCursor('default');
        // e.target.style.background = 'white';
        // for (let i=0;i<document.getElementById('list').children.length;i++) {
        //     document.getElementById('list').children[i].style.background = 'white';
        // }
    };

    return (
        <div>
            <List id="list" sx={{ width: '100%', maxWidth: 360 }}>
                {props.list.map((item) => (
                    <div>
                        <ListItem style={{ cursor: cursor }} alignItems="flex-start" onClick={handleClickOpen} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
                                <ListItemAvatar>
                                    <Avatar alt={item.name} src={`https://www.inf.ufpr.br/hvehrenfried/Treinamento/Dados/photo/${item['photo file']}`} />
                                </ListItemAvatar>
                                <ListItemText style={{marginTop:"15px"}} primary={item.name + " " + item.surname} />
                            {/* <Button variant="outlined" onClick={handleClickOpen}>
                            </Button> */}
                        </ListItem>
                        <Divider component="li" />
                    </div>
                ))}
            </List>
            <SimpleDialog
                onClose={handleClose}
                open={open}
            />
        </div>
    );
}
